var orderType = msg.payload;
flow.set('orderType', orderType);
return msg;

var orderType = flow.get('orderType');
var orderDuration = flow.get('duration');

// Vérification de la durée
node.warn('Valeur de la durée : ' + orderDuration);

var orders = flow.get('orders') || [];

var newOrder = {
    "order": orderType,
    "duration": orderDuration
};

orders.push(newOrder);
flow.set('orders', orders);

msg.payload = orders;
return msg;

var ID_Serie = flow.get("ID_Serie");
var ID_Bassin = flow.get("ID_Bassin");
var ID_Arene = flow.get("ID_Arene");
var ID_Sequence = flow.get("ID_Sequence");
var ID_Camera = flow.get("ID_Camera");
var location = flow.get("location");
var video_file_name = flow.get("video_file_name");
var PL_Name = flow.get("PL_Name");
var Config_Name = flow.get('Config_Name');
var video_Path = global.get("VIDEO_PATH");
var max_size_time = global.get("max_size_time");
var max_size_file = global.get("max_size_file");

var conf = {
    "pipeline_name": PL_Name,
    "ID_Serie": ID_Serie,
    "ID_Bassin": ID_Bassin,
    "ID_Arene": ID_Arene,
    "ID_Sequence": ID_Sequence,
    "ID_Camera": ID_Camera,
    "location": location,
    "video_file_name": video_file_name,
    "video_Path": video_Path, 
    "max_size_time": max_size_time,
    "max_size_file": max_size_file
};

// Récupérer les ordres enregistrés dans le flow
var orders = flow.get('orders') || [];

// Créer un objet avec "conf" et "orders"
var finalPayload = {
    "conf": conf,
    "orders": orders
};

// Formater le JSON avec des sauts de ligne
msg.payload = JSON.stringify(finalPayload, null, 2);

// Générer le nom du fichier à partir du Config_Name
var fileName = PL_Name + ".json";
var configPath = global.get("CONFIG_PATH");

msg.filename = configPath + fileName; // Chemin du fichier

node.warn(msg.filename);
// Retourner le message
return msg;

var duree = msg.payload;
flow.set('duration', duree);
return msg;

flow.set('orders', []);
msg.payload = 'Cache vidé, prêt pour le prochain ajout!';
return msg;

var exec_Path = global.get("pController");
var config_Path = global.get("CONFIG_PATH");
var video_Path = global.get("VIDEO_PATH");
msg.payload = video_Path +" - \n " + exec_Path + " - \n " + config_Path;
return msg;

var selectedFiles = global.get('fileListSelected') || [];
var dirConf = global.get('CONFIG_PATH');
var pController = global.get('pController');

var pipelinesStates = Array(6).fill(null);  // Tableau avec 6 éléments vides
flow.set('pipelinesStates', pipelinesStates);

node.warn(dirConf);

if (selectedFiles.length === 0) {
    node.warn("Aucun fichier sélectionné.");
    return null;
}

var threads = selectedFiles.map(file => {
    return {
        topic: "start_thread",
        payload: pController + " " + dirConf + file
    };
});

return [threads];

var payload = msg.payload;

// Vérifier que les informations nécessaires sont présentes dans le message
if (!payload || !payload.pipeline_name || !payload.state) {
    node.error("Message MQTT invalide: " + JSON.stringify(payload));
    return null;
}

// Initialiser ou récupérer le tableau des états des pipelines (taille fixe de 6 éléments)
var pipelinesStates = flow.get('pipelinesStates') || Array(6).fill(null);  // Tableau avec 6 éléments vides

// Identifier l'index de pipeline_name (par exemple: pipeline1 correspond à index 0, pipeline2 à index 1, etc.)
var index = parseInt(payload.pipeline_name.replace('pipeline', '')) - 1;

// Déterminer l'icone
var icone = "report";
var couleur = "blue";

node.warn(payload.state);

if (payload.state == "playing") {
    icone = "play_circle_outline";
    couleur = "green";
    node.warn("maj style playing");
    node.warn(couleur);

}

if (payload.state == "paused") {
    icone = "pause_circle_filled";
    couleur = "orange";
}

if (payload.state == "deleted" ) {
    icone = "fiber_manual_record";
    couleur = "red";
}

// Si l'index est valide (entre 0 et 5), on met à jour l'état du pipeline correspondant
if (index >= 0 && index < 6) {
    pipelinesStates[index] = {
        label: payload.pipeline_name,
        state: payload.state,
        //icon: (payload.state === 'deleted') ? 'delete_sweep' : '',
        icon: icone,
        color: couleur,
        timestamp: new Date().toLocaleString()
    };
} else {
    node.error("Nom de pipeline invalide: " + payload.pipeline_name);
    return null;
}

// Sauvegarder le tableau mis à jour dans le flow
flow.set('pipelinesStates', pipelinesStates);


// Passer le tableau mis à jour au message pour l'utiliser dans les UI ou autres nœuds
msg.payload = pipelinesStates;

return msg;
